import React, { Component } from 'react'
import axios from 'axios';

class PostForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
       name: "",
       city: "",
       age: "",
    }
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name] : e.target.value
    })
  }

  handleSubmit = (e) => {
    e.preventDefault()
    axios.post(`http://localhost:3006/contacts`, this.state)
      .then(res => {
        console.log(res)
      })
  }

  render() {
    const {name, city, age} = this.state

    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div>
            <label>Name</label>
            <input 
            type="text"
            name="name"
            value={name}
            onChange={this.handleChange} 
            />
          </div>
          <div>
            <label>City</label>
            <input 
            type="text"
            name="city"
            value={city}
            onChange={this.handleChange} 
            />
          </div>
          <div>
            <label>Age</label>
            <input 
            type="number"
            name="age"
            value={age}
            onChange={this.handleChange} 
            />
          </div>
          <div>
            <button type="submit">Submit</button>
          </div>
        </form>

      </div>
    )
  }
}

export default PostForm
