import React, { Component } from 'react';
import axios from 'axios';

export default class  postPosts extends Component {
    constructor(props) {
      super(props)


      this.state = {
        title: ''
      }

      this.onChangeUserName = this.onChangeUserName.bind(this);
      this.onSubmit = this.onSubmit.bind(this);
    }

  onChangeUserName(e) {
    console.log('Changing state')
    this.setState({ title: e.target.value })
  }

  onSubmit(e) {
    e.preventDefault()

    // console.log(this.state.title)

    const userObject = {
        name: this.state.title
    };

    axios.post('https://jsonplaceholder.typicode.com/posts', userObject)
        .then((res) => {
            console.log('Data:', res.data)
        }).catch((error) => {
            console.log(error)
        });

    this.setState({ name: '' })
    console.log(this.state.title)
}

  render() {
    return (
      <div className="main">
        <form action="" onSubmit={this.onSubmit}>
        <input value={this.state.title} onChange={this.onChangeUserName} type="text" placeholder="name" />
        <button type="submit" value="Create Post">Send</button>
        </form>
      </div>
    )
  }
}
